#import "UI7Toolbar.h"

@implementation UI7Toolbar

- (id)initWithFrame:(CGRect)frame
{
    self = [super initWithFrame:frame];
    if (self) {
        self.tintColor = [UIColor greenColor];
    }
    return self;
}

